# Tonelli-Shanks calculates r


def tonelli_shanks(a, p):
    # Trường hợp đặc biệt p = 2
    if p == 2:
        return a % p
    
    # Kiểm tra xem a có phải bình phương mod p không
    if pow(a, (p - 1) // 2, p) != 1:
        return None  # vô nghiệm
    
    # Bước 1: tách p - 1 = Q * 2^S
    Q, S = p - 1, 0
    while Q % 2 == 0:
        Q //= 2
        S += 1

    # Bước 2: tìm z là non-residue
    z = 2
    while pow(z, (p - 1) // 2, p) != p - 1:
        z += 1

    # Bước 3: khởi tạo
    M = S
    c = pow(z, Q, p)
    t = pow(a, Q, p)
    R = pow(a, (Q + 1) // 2, p)

    # Bước 4: lặp
    while t != 1:
        i = 0
        temp = t
        while temp != 1:
            temp = pow(temp, 2, p)
            i += 1
            if i == M:
                return None

        b = pow(c, 2 ** (M - i - 1), p)
        M = i
        c = pow(b, 2, p)
        t = (t * c) % p
        R = (R * b) % p

    return R

